import React,{Component} from 'react';
import './App.css';

class VoteField extends Component {

    constructor(props) {
        super(props);
        this.state.languageName = props.languageName;
    }

    state = {count: 0, languageName: undefined};

    incrementCount = () => this.setState({count: this.state.count+1});

    render() {
        return(
            <div className="language">
                <div className="voteCount">{this.state.count}</div>
                <div className="language">{this.state.languageName}</div>
                <button className="button" onClick={this.incrementCount}>Vote</button>
            </div>
        )
    }
}

export default VoteField;
