import React,{Component} from 'react';
import './App.css';

class VoteField extends Component {

    constructor(props) {
        super(props);
        this.state.languageName = props.languageName;
    }

    state = {count: 0, languageName: undefined};

    incrementCount = () => this.setState({count: this.state.count+1});

    render() {
        return(
            <div>
                <button className="button" onClick={this.incrementCount}>{this.state.count} {this.state.languageName}</button>
            </div>
        )
    }
}

export default VoteField;
